// A simple function to get translations.
// In a real app, this would be more sophisticated, likely involving context or a library.
// The locale would be determined from the URL, user preferences, or global context.
export const getTranslator = (locale: Locale = defaultLocale) => {
  const selectedTranslations = translations[locale] || translations[defaultLocale];
  return (key: string, fallback?: string): string => {
    const translation = selectedTranslations[key];
    if (translation === undefined) {
        // Fallback for missing keys during development for other languages
        if (locale !== 'en' && translations['en'][key]) {
            return translations['en'][key]; // Return English if key exists there
        }
        return fallback || key; // Return fallback or key itself
    }
    return translation;
  };
};

    